INSERT INTO businesses (name, slug, description, category_id, area_id, phone, email, website, address, latitude, longitude, is_placeholder, rating, notes, social_links)
VALUES
  ('Cappuccino Grand Cafe Santa Ponsa', 'cappuccino-santa-ponsa', 'Part of the beloved Cappuccino chain, this Santa Ponsa location offers premium coffee, fresh pastries, all-day brunch, and Mediterranean light meals in a sophisticated interior with a large terrace.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', '+34 971 69 25 14', '', 'grupocappuccino.com', 'Avinguda Rei Jaume I, 07180 Santa Ponsa', 39.5077, 2.4776, false, 4.4, 'Chain cafe with premium feel. All-day brunch. Free wifi. Outdoor terrace. Great for remote work.', '{"instagram": "https://instagram.com/grupocappuccino"}'::jsonb),
  ('The Coffee Lab', 'the-coffee-lab-santa-ponsa', 'Specialty coffee shop in Santa Ponsa bringing third-wave coffee culture to Calvia. Single-origin beans, expert baristas, and a modern minimalist space perfect for coffee enthusiasts and remote workers.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', '+34 871 57 41 22', '', '', 'Via de la Creu, 07180 Santa Ponsa', 39.5083, 2.4790, false, 4.8, 'Specialty coffee. Single origin beans. Oat and almond milk. Pastries. Free wifi. Laptop-friendly.', '{}'::jsonb),
  ('Cafe Nautico Palmanova', 'cafe-nautico-palmanova', 'Seafront cafe on the Palmanova promenade. Fresh coffee, freshly squeezed juices, light breakfast and lunch options with uninterrupted Mediterranean views. Perfect for a morning coffee by the sea.', 'dad7059a-3860-4e6e-b178-157793beabc8', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 16 33', '', '', 'Passeig de la Mar, 07181 Palmanova', 39.5227, 2.5333, false, 4.3, 'Seafront cafe. Fresh juices. Breakfast and light lunch. Sea views. Open early.', '{}'::jsonb),
  ('Dolce Vita Cafe', 'dolce-vita-cafe-portals', 'Italian-inspired cafe in Portals Nous offering espresso, cappuccino, and Italian pastries alongside light Mediterranean meals. A charming spot near Puerto Portals for morning coffee or afternoon aperitivo.', 'dad7059a-3860-4e6e-b178-157793beabc8', '90784574-076e-471d-b61a-d18422d0f69f', '+34 971 67 59 80', '', '', 'Carrer d''Oratori, 07181 Portals Nous', 39.5360, 2.5633, false, 4.5, 'Italian cafe culture. Espresso specialist. Fresh cannoli. Aperitivo hour. Terrace seating.', '{}'::jsonb),
  ('Paneria Bakery & Cafe', 'paneria-bakery-cafe', 'Artisan bakery and cafe in Palmanova. Fresh-baked bread, croissants, pastries, and sandwiches made daily on-site. Combined with excellent coffee for a complete breakfast or lunch experience.', 'dad7059a-3860-4e6e-b178-157793beabc8', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 20 55', '', '', 'Carrer de la Duquessa, 07181 Palmanova', 39.5240, 2.5340, false, 4.5, 'Artisan bakery. Fresh baked daily. Sourdough bread. Croissants. Sandwiches. Coffee and juices.', '{}'::jsonb),
  ('Cafe del Sol', 'cafe-del-sol-peguera', 'Welcoming cafe in Peguera with a sunny terrace and a menu of fresh coffee, smoothies, and light Mediterranean bites. A relaxed spot for breakfast or an afternoon break while exploring Peguera.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'aedc2a15-2949-4522-9239-4bbe35580947', '+34 971 68 63 10', '', '', 'Avinguda de Peguera, 07160 Peguera', 39.5335, 2.4405, false, 4.3, 'Sunny terrace cafe. Smoothies and fresh juices. Breakfast menu. Free wifi.', '{}'::jsonb),
  ('La Panaderia', 'la-panaderia-calvia', 'Traditional bakery and cafe in Calvia Village serving freshly baked bread, ensaimadas, and local pastries alongside quality coffee. A village institution for morning coffee and fresh baked goods.', 'dad7059a-3860-4e6e-b178-157793beabc8', '5ea18376-376c-4f73-bef6-f5111b745dd4', '+34 971 67 03 22', '', '', 'Carrer Major, 07184 Calvia', 39.5637, 2.5065, false, 4.4, 'Traditional bakery. Ensaimadas daily. Village institution. Coffee and pastries. Open from 7am.', '{}'::jsonb),
  ('Sam''s Cafe', 'sams-cafe-magaluf', 'Tourist-friendly brunch and coffee spot in Magaluf. Full English breakfast, pancakes, smoothie bowls, and specialty coffee. A welcome respite from the party scene with a chill daytime atmosphere.', 'dad7059a-3860-4e6e-b178-157793beabc8', '2a8543a1-49fb-4656-a49f-ec59320b37e5', '+34 971 13 14 02', '', '', 'Carrer Torrenova, 07182 Magaluf', 39.5090, 2.5282, false, 4.4, 'Brunch specialist. Full English. Pancakes and waffles. Smoothie bowls. Open 8am-5pm.', '{"instagram": "https://instagram.com/samscafemagaluf"}'::jsonb),
  ('Cafe Portals', 'cafe-portals', 'Marina-side cafe at Puerto Portals. Premium coffee, fresh pastries, and light lunch options with views over the yachts. The perfect spot for people-watching in one of Mallorca''s most glamorous marinas.', 'dad7059a-3860-4e6e-b178-157793beabc8', '90784574-076e-471d-b61a-d18422d0f69f', '+34 971 67 91 80', '', '', 'Puerto Portals, 07181 Portals Nous', 39.5315, 2.5600, false, 4.3, 'Marina-side cafe. Yacht watching. Premium coffee. Light lunch menu.', '{}'::jsonb),
  ('Bean & Brew', 'bean-and-brew', 'Specialty coffee bar in Santa Ponsa catering to the digital nomad and remote worker community. Excellent espresso, comfortable workspace, fast wifi, and a menu of healthy light meals.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', '+34 871 03 28 15', '', '', 'Via de la Creu, 07180 Santa Ponsa', 39.5085, 2.4792, false, 4.7, 'Digital nomad friendly. Fast wifi. Power outlets. Specialty coffee. Healthy meals. Quiet workspace.', '{}'::jsonb),
  ('Cafe Paris', 'cafe-paris-palmanova', 'European-style cafe in Palmanova bringing a touch of Parisian charm to the promenade. French pastries, crepes, premium coffee, and a terrace perfect for leisurely breakfasts.', 'dad7059a-3860-4e6e-b178-157793beabc8', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 17 90', '', '', 'Passeig del Mar, 07181 Palmanova', 39.5223, 2.5330, false, 4.2, 'French-style cafe. Crepes and croissants. European breakfast menu. Terrace seating.', '{}'::jsonb),
  ('Bon Lloc Cafe', 'bon-lloc-cafe', 'Organic and health-focused cafe in Calvia Village. Smoothie bowls, avocado toast, organic coffee, and plant-based options. A modern wellness-oriented cafe in the heart of the traditional village.', 'dad7059a-3860-4e6e-b178-157793beabc8', '5ea18376-376c-4f73-bef6-f5111b745dd4', '+34 871 57 42 66', '', '', 'Placa Major, 07184 Calvia', 39.5640, 2.5067, false, 4.6, 'Organic cafe. Smoothie bowls. Vegan options. Cold-pressed juices. Eco-friendly packaging.', '{}'::jsonb),
  ('Cafe Es Capdella', 'cafe-es-capdella', 'Village square cafe in Es Capdella, one of Calvia''s most charming inland villages. Traditional coffee, local pastries, and simple Mallorcan breakfast served in the peaceful village plaza.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'a1519a3d-ebe8-48f6-b77d-bea6a9e7aa0f', '+34 971 67 20 15', '', '', 'Placa de Es Capdella, 07196 Es Capdella', 39.5770, 2.4703, false, 4.3, 'Village plaza cafe. Traditional atmosphere. Local pastries. Peaceful setting. Cash preferred.', '{}'::jsonb),
  ('Cafe La Terraza', 'cafe-la-terraza-peguera', 'Terrace cafe in Peguera with panoramic views over the bay. Morning coffee, fresh juices, and a Mediterranean brunch menu. An elevated spot for enjoying Peguera''s beautiful coastline.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'aedc2a15-2949-4522-9239-4bbe35580947', '+34 971 68 64 88', '', '', 'Carrer Gavines, 07160 Peguera', 39.5338, 2.4395, false, 4.4, 'Terrace cafe with sea views. Fresh juices. Mediterranean brunch. Relaxed atmosphere.', '{}'::jsonb),
  ('Morning Glory Cafe', 'morning-glory-cafe', 'Breakfast and brunch specialist in Costa de la Calma. Fresh-baked goods, eggs benedict, pancakes, and specialty coffee in a bright, modern space. Popular weekend brunch destination.', 'dad7059a-3860-4e6e-b178-157793beabc8', 'dd536f8d-1ece-4df6-b32e-21a992ac307b', '+34 871 03 29 44', '', '', 'Avinguda Son Caliu, 07183 Costa de la Calma', 39.5162, 2.4665, false, 4.5, 'Breakfast and brunch specialist. Eggs benedict. Fresh pancakes. Specialty coffee. Open 7am-3pm.', '{}'::jsonb)
INSERT INTO businesses (name, slug, description, category_id, area_id, phone, email, website, address, latitude, longitude, is_placeholder, rating, notes, social_links)
VALUES
  ('Nikki Beach Mallorca', 'nikki-beach-mallorca', 'The world-famous Nikki Beach brand in Magaluf. An all-day beach club experience featuring a stunning pool, VIP day beds, gourmet dining, and international DJs. The most talked-about beach club destination in Calvia.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', '2a8543a1-49fb-4656-a49f-ec59320b37e5', '+34 971 12 39 62', '', 'nikkibeach.com/mallorca', 'Av. Notario Alemany 1, 07182 Magaluf', 39.5098, 2.5270, false, 4.3, 'World-famous beach club. Pool and beach. Day bed reservations required. International DJs. Sunday brunch legendary. Open May-October.', '{"instagram": "https://instagram.com/nikkibeachmallorca"}'::jsonb),
  ('Beso Beach Club', 'beso-beach-club', 'Trendy beach club in Palmanova with a strong Instagram presence. Day beds, poolside cocktails, Mediterranean food, and sunset DJ sessions create one of Calvia''s most desirable beach experiences.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', '611170cd-47c1-4060-a226-fad26428df6a', '+34 871 03 25 80', '', 'besobeach.com', 'Paseo del Mar, 07181 Palmanova', 39.5232, 2.5352, false, 4.5, 'Trendy beach club. Day beds and sunloungers. Pool. DJs from 2pm. Mediterranean kitchen. Seasonal May-Oct.', '{"instagram": "https://instagram.com/besobeach"}'::jsonb),
  ('Purobeach Illetas', 'purobeach-illetas', 'Luxury beach club experience in Ses Illetes. Part of the Puro brand, offering designer sun loungers, a stylish pool, gourmet Mediterranean dining, and curated music programming in an exclusive coastal setting.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', 'f3fa2a2d-fd7d-4bb0-96e7-5384ca1c76c7', '+34 971 70 32 35', '', 'purobeach.com', 'Paseo de Illetas 60, 07015 Ses Illetes', 39.5465, 2.5843, false, 4.4, 'Luxury beach club. Designer loungers. Infinity pool. Signature cocktails. Full restaurant. Open May-October.', '{"instagram": "https://instagram.com/purobeach"}'::jsonb),
  ('Balneario Illetas', 'balneario-illetas', 'Historic beach club in Ses Illetes that has been a beloved destination for generations. Traditional Mallorcan beach club experience with excellent seafood, sun loungers, and crystal-clear waters.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', 'f3fa2a2d-fd7d-4bb0-96e7-5384ca1c76c7', '+34 971 40 12 31', '', '', 'Playa de Illetas, 07015 Ses Illetes', 39.5460, 2.5840, false, 4.3, 'Historic beach club. Seafood restaurant. Sun loungers. Clear water beach. Family-friendly. Open year-round.', '{}'::jsonb),
  ('Gran Folies Beach Club', 'gran-folies-beach-club', 'Spectacular cliff-side beach club in Cala Vinyes. Built into the rock face with terraces cascading down to the sea. Fresh seafood, cocktails, and some of the most dramatic views in all of Calvia.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', 'c4f6258b-10bd-4878-84f6-90847edd2d25', '+34 971 13 12 10', '', '', 'Cala Vinyes, 07181', 39.5008, 2.4850, false, 4.6, 'Cliff-side beach club. Dramatic views. Fresh seafood. Cocktail bar. Access via steps. Reservation advised.', '{"instagram": "https://instagram.com/granfolies"}'::jsonb),
  ('Zhero Beach Club', 'zhero-beach-club', 'Boutique beach club attached to the Zhero Hotel in Palmanova. Minimalist design, quality cocktails, curated music, and Mediterranean fusion food in a chic beachside setting.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 25 36', '', 'zherohotel.com', 'Paseo del Mar, 07181 Palmanova', 39.5234, 2.5354, false, 4.4, 'Boutique beach club. Minimalist design. Quality cocktails. Mediterranean fusion food. Hotel guests priority.', '{"instagram": "https://instagram.com/zherobeach"}'::jsonb),
  ('UM Beach House', 'um-beach-house', 'Premium day club experience on Palmanova beach. UM combines gourmet food, craft cocktails, and premium music curation in a stylish open-air venue that sets the standard for daytime entertainment.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', '611170cd-47c1-4060-a226-fad26428df6a', '+34 871 03 27 55', '', '', 'Paseo del Mar, 07181 Palmanova', 39.5235, 2.5356, false, 4.5, 'Premium day club. Gourmet food. Craft cocktails. Music programming. Beachfront. Seasonal.', '{"instagram": "https://instagram.com/umbeachhouse"}'::jsonb),
  ('Kalima Beach Club', 'kalima-beach-club', 'Beach club attached to the ME Mallorca hotel in Santa Ponsa. Stylish poolside setting with day beds, Mediterranean food, signature cocktails, and weekend DJ sessions.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', '+34 971 69 35 10', '', '', 'Carrer Llaut, 07180 Santa Ponsa', 39.5068, 2.4770, false, 4.3, 'Hotel beach club. Pool and beach access. Day beds. Mediterranean menu. DJ weekends.', '{}'::jsonb),
  ('Lobster Club Port Adriano', 'lobster-club-port-adriano', 'Premium seafood beach club at Port Adriano marina. Lobster, champagne, and Mediterranean seafood in Philippe Starck''s stunning marina design. Upscale dining meets beach club luxury.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', 'eac49f01-a0e4-402d-9261-678b7be034c2', '+34 971 23 65 50', '', '', 'Port Adriano, 07180', 39.4865, 2.4658, false, 4.4, 'Premium seafood beach club. Lobster and champagne. Philippe Starck marina. Upscale dress code.', '{}'::jsonb),
  ('Virtual Club Portals', 'virtual-club-portals', 'Beach experience at Puerto Portals marina. Day loungers, cocktails, tapas, and a front-row seat to the superyacht scene. Popular with the international set for long afternoon sessions.', '6da52cc6-9c43-457b-8c45-5e62a8a75f19', '90784574-076e-471d-b61a-d18422d0f69f', '+34 971 67 60 40', '', '', 'Puerto Portals, 07181 Portals Nous', 39.5314, 2.5597, false, 4.2, 'Marina beach experience. Day loungers. Cocktails and tapas. Yacht watching. Afternoon sessions.', '{}'::jsonb)
INSERT INTO businesses (name, slug, description, category_id, area_id, phone, email, website, address, latitude, longitude, is_placeholder, rating, notes, social_links)
VALUES
  ('Portals Hills Boutique Hotel', 'portals-hills-boutique-hotel', 'Five-star boutique hotel in Portals Nous with stunning infinity pool, spa, and Mediterranean views. A design-forward property combining contemporary luxury with Mallorcan warmth, minutes from Puerto Portals.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '90784574-076e-471d-b61a-d18422d0f69f', '+34 971 67 00 50', '', 'portalshills.com', 'Carrer Mestre J. Tous Ferrer 5, 07181 Portals Nous', 39.5365, 2.5640, false, 4.8, '5-star boutique hotel. Infinity pool. Spa. Mediterranean views. Design rooms. Near Puerto Portals marina.', '{"instagram": "https://instagram.com/portalshills"}'::jsonb),
  ('Gran Melia de Mar', 'gran-melia-de-mar', 'Iconic 5-star luxury hotel on the exclusive Ses Illetes beachfront. Adults-only Red Level suites, award-winning spa, gourmet restaurants, and direct beach access. One of Mallorca''s most prestigious addresses.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', 'f3fa2a2d-fd7d-4bb0-96e7-5384ca1c76c7', '+34 971 40 25 11', '', 'melia.com', 'Paseo de Illetas 7, 07015 Ses Illetes', 39.5458, 2.5838, false, 4.7, '5-star luxury. Adults-only option. Award-winning spa. Beachfront. Multiple restaurants. Seasonal.', '{"instagram": "https://instagram.com/granmeliademar"}'::jsonb),
  ('Kimpton Aysla Mallorca', 'kimpton-aysla-mallorca', 'Stunning 5-star IHG hotel in Palmanova. Contemporary design, rooftop pool with sea views, multiple dining venues, and a wellness centre. Redefining luxury hospitality on the southwest coast.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 12 03 00', '', 'ihg.com', 'Calle Tinent Gabriel Riera Alemany 7, 07181 Palmanova', 39.5250, 2.5360, false, 4.6, '5-star IHG property. Rooftop pool. Multiple restaurants. Wellness centre. Walking distance to beach.', '{"instagram": "https://instagram.com/kimptonaysla"}'::jsonb),
  ('Melia South Beach Magaluf', 'melia-south-beach-magaluf', 'Stylish beachfront hotel in Magaluf from the Melia Hotels group. Modern design, direct beach access, rooftop bar, and a location that captures the energy of the revitalized Magaluf coastline.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '2a8543a1-49fb-4656-a49f-ec59320b37e5', '+34 971 12 00 84', '', 'melia.com', 'Carrer Notari Alemany 1, 07182 Magaluf', 39.5095, 2.5275, false, 4.4, '4-star design hotel. Beachfront. Rooftop bar. Pool. Modern rooms. Magaluf beach location.', '{"instagram": "https://instagram.com/meliasouthbeach"}'::jsonb),
  ('Hotel BonSol Resort & Spa', 'hotel-bonsol', 'Family-owned 4-star resort in Ses Illetes with tropical gardens, multiple pools, spa, and private beach access. A beloved institution that has welcomed guests for over 60 years.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', 'f3fa2a2d-fd7d-4bb0-96e7-5384ca1c76c7', '+34 971 40 21 11', '', 'hotelbonsol.es', 'Paseo de Illetas 30, 07015 Ses Illetes', 39.5463, 2.5842, false, 4.5, '4-star resort. Tropical gardens. Multiple pools. Spa. Private beach. Family-owned since 1953.', '{"instagram": "https://instagram.com/hotelbonsol"}'::jsonb),
  ('Zafiro Palace Palmanova', 'zafiro-palace-palmanova', 'Large 5-star resort in Palmanova with expansive pool complex, multiple restaurants, spa, and entertainment facilities. A comprehensive resort experience ideal for families and couples.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 28 00', '', 'zafirohotels.com', 'Avinguda de S''Olivera, 07181 Palmanova', 39.5255, 2.5365, false, 4.5, '5-star resort. Large pool complex. Multiple restaurants. Spa and wellness. Family-friendly. All-inclusive option.', '{}'::jsonb),
  ('H10 Casa del Mar', 'h10-casa-del-mar', 'Beachfront 4-star hotel in Santa Ponsa with direct access to the sandy beach. Renovated rooms, rooftop pool and terrace, spa, and Mediterranean restaurant. Excellent location for beach holidays.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', '+34 971 69 16 12', '', 'hotelh10.com', 'Via Creu 37, 07180 Santa Ponsa', 39.5065, 2.4765, false, 4.4, '4-star beachfront. Rooftop pool. Spa. Mediterranean restaurant. Direct beach access.', '{}'::jsonb),
  ('Hotel Zhero', 'hotel-zhero', 'Boutique design hotel in Palmanova. Minimalist architecture, curated art collection, rooftop sundeck, and a beach club experience. For design-conscious travellers seeking something beyond mainstream hospitality.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 25 35', '', 'zherohotel.com', 'Carrer Torrenova, 07181 Palmanova', 39.5236, 2.5355, false, 4.5, 'Boutique design hotel. Minimalist. Art collection. Rooftop sundeck. Beach club. Adults preferred.', '{"instagram": "https://instagram.com/zherohotel"}'::jsonb),
  ('INNSiDE by Melia Calvia Beach', 'innside-calvia-beach', 'Design hotel in Magaluf from the INNSiDE by Melia brand. Rooftop pool and restaurant with panoramic sea views, modern rooms, and a location capturing the new spirit of Magaluf''s upscale transformation.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '2a8543a1-49fb-4656-a49f-ec59320b37e5', '+34 971 12 08 00', '', 'melia.com', 'Calvia Beach, 07182 Magaluf', 39.5097, 2.5266, false, 4.5, '4-star design hotel. Rooftop pool. Panoramic restaurant. Modern design. Near beach.', '{}'::jsonb),
  ('Fergus Style Palmanova', 'fergus-style-palmanova', 'Adults-only boutique hotel in Palmanova. Stylish rooms, pool with sun terrace, and a tranquil atmosphere just minutes from the beach and local restaurants. Perfect for couples seeking relaxation.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 13 00', '', 'fergushotels.com', 'Carrer de Torrenova, 07181 Palmanova', 39.5238, 2.5358, false, 4.3, 'Adults-only. Boutique style. Pool with terrace. Near beach. Quiet atmosphere. Couples focused.', '{}'::jsonb),
  ('La Concha Soul Boutique Hotel', 'la-concha-soul-peguera', 'Charming boutique hotel in Peguera offering affordable luxury. Individually designed rooms, rooftop terrace with mountain views, and a central location walking distance from three beaches.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', 'aedc2a15-2949-4522-9239-4bbe35580947', '+34 971 68 60 50', '', '', 'Carrer dels Pins, 07160 Peguera', 39.5332, 2.4395, false, 4.4, 'Boutique hotel. Individually designed rooms. Rooftop terrace. Mountain views. Near 3 beaches.', '{}'::jsonb),
  ('Hotel Creta Paguera', 'hotel-creta-paguera', 'Popular mid-range hotel in Peguera with outdoor pool, sun terrace, and a comfortable base for exploring the southwest coast. Good value accommodation close to Peguera''s beaches and restaurants.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', 'aedc2a15-2949-4522-9239-4bbe35580947', '+34 971 68 68 00', '', '', 'Carrer Gavines, 07160 Peguera', 39.5337, 2.4398, false, 4.2, 'Mid-range hotel. Outdoor pool. Sun terrace. Near beaches. Good value. Half-board option.', '{}'::jsonb),
  ('Elba Sunset Mallorca', 'elba-sunset-mallorca', 'Thalasso spa hotel in Palmanova offering a unique wellness experience. Sea water therapy, heated pools, panoramic terrace, and Mediterranean views. Ideal for relaxation and rejuvenation breaks.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '611170cd-47c1-4060-a226-fad26428df6a', '+34 971 68 26 90', '', 'elbahotels.com', 'Carrer de Torrenova, 07181 Palmanova', 39.5237, 2.5357, false, 4.4, 'Thalasso spa hotel. Sea water therapy. Heated pools. Panoramic views. Wellness packages.', '{}'::jsonb),
  ('The Donna Portals', 'the-donna-portals', 'Five-star luxury hotel in Portals Nous offering supreme elegance. Expansive suites, world-class spa, rooftop infinity pool, and Michelin-quality dining. The pinnacle of accommodation in Calvia.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '90784574-076e-471d-b61a-d18422d0f69f', '+34 971 67 12 00', '', '', 'Carrer d''Andratx, 07181 Portals Nous', 39.5362, 2.5636, false, 4.7, '5-star luxury. Suites. Rooftop infinity pool. World-class spa. Fine dining. Near Puerto Portals.', '{}'::jsonb),
  ('Hotel Bendinat', 'hotel-bendinat', 'Elegant 4-star hotel in the exclusive Bendinat area. Set in beautiful gardens with sea views, outdoor pool, spa, and refined Mediterranean restaurant. A peaceful retreat favoured by discerning travellers.', 'e1fee07c-cc3f-4e5f-bc71-377fda3839bf', '68dd13d8-2b72-4a0c-8477-c0e265a6f3de', '+34 971 67 57 25', '', 'hotelbendinat.es', 'Carretera Andratx 11, 07015 Bendinat', 39.5450, 2.5688, false, 4.5, '4-star elegant hotel. Gardens. Sea views. Pool. Spa. Restaurant with terrace. Peaceful location.', '{}'::jsonb)
