INSERT INTO businesses (name, slug, description, address, phone, website, category_id, area_id, rating, notes) VALUES
('Dive & Fun Mallorca', 'dive-and-fun-mallorca', 'PADI 5-star dive centre based in Santa Ponsa offering discovery dives, certification courses from Open Water to Divemaster, and guided dive excursions to the El Toro Marine Reserve. Equipment hire and underwater photography available.', 'Via de la Cruz, 2, Santa Ponsa', '+34 971 691 213', 'https://www.diveandfun.com', 'fff617f0-5928-4339-bca2-5e9199303e49', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', 4.7, 'Established dive centre with multilingual instructors'),
('Big Blue Diving Mallorca', 'big-blue-diving-mallorca', 'Professional PADI dive school in Palmanova offering daily dive trips, snorkelling excursions, and courses for all levels. Specialising in small groups for a personalised experience. Equipment provided.', 'Paseo del Mar, 22, Palmanova', '+34 971 681 445', 'https://www.bigbluediving.net', 'fff617f0-5928-4339-bca2-5e9199303e49', '611170cd-47c1-4060-a226-fad26428df6a', 4.8, 'Small groups and personal attention'),
('Mallorca SUP Academy', 'mallorca-sup-academy', 'Stand-up paddleboarding school in Palmanova offering lessons, guided tours along the coast, SUP yoga, and board rental. Suitable for beginners through to advanced paddlers. Sunset SUP tours available in summer.', 'Playa de Palmanova, Palmanova', '+34 626 789 123', '', 'fff617f0-5928-4339-bca2-5e9199303e49', '611170cd-47c1-4060-a226-fad26428df6a', 4.6, 'Popular sunset tours along the coast'),
('Calvia Kayak Tours', 'calvia-kayak-tours', 'Guided kayak excursions exploring Calvia''s coastline, sea caves, and hidden coves. Half-day and full-day trips available with snorkelling stops. Transparent kayaks available for a unique experience.', 'Playa de Santa Ponsa, Santa Ponsa', '+34 634 567 890', '', 'fff617f0-5928-4339-bca2-5e9199303e49', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', 4.5, 'Transparent kayak experience'),
('Magaluf Jet Ski Safari', 'magaluf-jet-ski-safari', 'Jet ski rental and guided safari tours from Magaluf beach. Explore the coastline at speed with 30-minute, 1-hour, and 2-hour tour options. No experience necessary â€” instruction provided.', 'Playa de Magaluf, Magaluf', '+34 649 321 654', '', 'fff617f0-5928-4339-bca2-5e9199303e49', '2a8543a1-49fb-4656-a49f-ec59320b37e5', 4.3, 'Popular with tourists, coastal safari tours'),
('Sail & Surf Mallorca', 'sail-surf-mallorca', 'Sailing school and windsurf centre at Santa Ponsa beach. Dinghy sailing, catamaran courses, windsurfing, and wingfoil lessons. RYA-accredited training centre with qualified British and Spanish instructors.', 'Club Nautico Santa Ponsa, Santa Ponsa', '+34 971 693 456', '', 'fff617f0-5928-4339-bca2-5e9199303e49', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', 4.6, 'RYA accredited sailing school'),
('Peguera Dive Centre', 'peguera-dive-centre', 'Friendly dive school in Peguera specialising in small-group dives among the local rock formations and marine life. PADI and SSI courses available. German and English spoken fluently. Night dives arranged on request.', 'Carrer Pins, 8, Peguera', '+34 971 686 234', '', 'fff617f0-5928-4339-bca2-5e9199303e49', 'aedc2a15-2949-4522-9239-4bbe35580947', 4.7, 'Popular with German-speaking divers'),
('H2O Sports Mallorca', 'h2o-sports-mallorca', 'Full-service water sports operator on Magaluf beach. Parasailing, banana boat rides, tube rides, flyboarding, and paddleboard rental. Fun for all ages with safety-first approach and experienced operators.', 'Playa de Magaluf, Magaluf', '+34 622 456 789', '', 'fff617f0-5928-4339-bca2-5e9199303e49', '2a8543a1-49fb-4656-a49f-ec59320b37e5', 4.2, 'Wide range of beach activities'),
('Portals Vells Snorkel Tours', 'portals-vells-snorkel-tours', 'Guided snorkelling excursions to the crystal-clear coves of Portals Vells. Equipment provided including masks, fins, and wetsuits. Suitable for beginners and families. Boat transfers from Magaluf available.', 'Portals Vells, Calvia', '+34 656 234 567', '', 'fff617f0-5928-4339-bca2-5e9199303e49', '2a8543a1-49fb-4656-a49f-ec59320b37e5', 4.5, 'Family-friendly snorkelling trips'),
('Mallorca Sailing Experience', 'mallorca-sailing-experience', 'Luxury catamaran and sailing yacht excursions departing from Port Adriano. Half-day, full-day, and sunset cruises along the Calvia coast. Includes swimming stops, snorkelling equipment, and catering.', 'Port Adriano, El Toro', '+34 971 232 567', '', 'fff617f0-5928-4339-bca2-5e9199303e49', 'eac49f01-a0e4-402d-9261-678b7be034c2', 4.8, 'Premium catamaran excursions')
INSERT INTO businesses (name, slug, description, address, phone, website, category_id, area_id, rating, notes) VALUES
('Taxi Calvia', 'taxi-calvia', 'Official taxi service covering the entire Calvia municipality. 24/7 availability for airport transfers, local journeys, and excursions. Fleet of modern vehicles including minivans for groups.', 'Calvia Vila', '+34 971 135 765', '', '542bb036-18c1-4f60-af78-8edb87e01a50', '5ea18376-376c-4f73-bef6-f5111b745dd4', 4.3, 'Official Calvia taxi service'),
('Mallorca Airport Transfers', 'mallorca-airport-transfers', 'Reliable private transfer service between Palma airport and all Calvia destinations. Pre-booked, fixed-price transfers with meet-and-greet service. Child seats available on request.', 'Palmanova', '+34 607 890 123', '', '542bb036-18c1-4f60-af78-8edb87e01a50', '611170cd-47c1-4060-a226-fad26428df6a', 4.6, 'Pre-booked airport transfer specialist'),
('TIB Bus Service - Calvia Routes', 'tib-bus-calvia', 'Public bus network connecting Calvia towns to Palma and surrounding areas. Key routes include 104 (Palmanova-Magaluf), 107 (Santa Ponsa-Peguera), and connections to Palma city and airport.', 'Multiple stops across Calvia', '+34 971 177 777', 'https://www.tib.org', '542bb036-18c1-4f60-af78-8edb87e01a50', '611170cd-47c1-4060-a226-fad26428df6a', 3.8, 'Public transport across Calvia'),
('Mallorca VTC Premium', 'mallorca-vtc-premium', 'Premium private hire vehicle service in Calvia. Mercedes and BMW fleet for airport transfers, special events, and island excursions. English, German, and Spanish speaking drivers.', 'Santa Ponsa', '+34 618 345 678', '', '542bb036-18c1-4f60-af78-8edb87e01a50', 'e770e3e1-c9e3-468d-a024-d65255b48c5c', 4.7, 'Premium chauffeur service'),
('Calvia Bike Rental', 'calvia-bike-rental', 'Bicycle and e-bike rental in Palmanova and Santa Ponsa. Road bikes, mountain bikes, e-bikes, and children''s bikes available by the day or week. Helmets, locks, and route maps included.', 'Paseo del Mar, 45, Palmanova', '+34 971 681 890', '', '542bb036-18c1-4f60-af78-8edb87e01a50', '611170cd-47c1-4060-a226-fad26428df6a', 4.5, 'Full range of rental bikes including e-bikes')
